+package fleetctl
+
+import (
+	"encoding/json"
+	"errors"
+	"fmt"
+	"io"
+	"maps"
+	"os"
+	"path/filepath"
+	"slices"
+	"strings"
+
+	"github.com/ghodss/yaml"
+	"github.com/urfave/cli/v2"
+)
+
+var w io.Writer = os.Stdout
+
+var transformations = map[string]func([]string) error{
+	"software-fields-to-team": func(inputFiles []string) error {
+		for _, file := range inputFiles {
+			// var err error
+			// var b []byte
+			fmt.Fprintf(w, "[+] Transforming file '%s'\n", file)
+
+			b, err := os.ReadFile(file)
+			if err != nil {
+				return fmt.Errorf("error reading file %s: %w", file, err)
+			}
+			var top map[string]json.RawMessage
+			if err := yaml.Unmarshal(b, &top); err != nil {
+				return fmt.Errorf("failed to unmarshal file %s: %w", file, err)
+			}
+
+			for key, value := range top {
+				fmt.Printf("%s: %s\n", key, string(value))
+			}
+		}
+		return nil
+	},
+}
+
+func gitopsTransformationsCommand() *cli.Command {
+	var (
+		flFilenames       cli.StringSlice
+		outputDirectory   string
+		flTransformations cli.StringSlice
+		force             bool
+	)
+
+	return &cli.Command{
+		Name:        "gitops-transformations",
+		Usage:       "Apply transformations to GitOps configuration files.",
+		UsageText:   `fleetctl gitops-transformations [options]`,
+		Description: "Perform transformations on existing GitOps configuration files for Fleet.",
+		Flags: []cli.Flag{
+			&cli.StringSliceFlag{
+				Name:        "f",
+				Required:    true,
+				EnvVars:     []string{"FILENAME"},
+				Destination: &flFilenames,
+				Usage:       "The file(s) with the GitOps configuration.",
+			},
+			&cli.StringFlag{
+				Name:        "dir",
+				Required:    true,
+				Destination: &outputDirectory,
+				Usage:       "The directory to write transformed GitOps configuration file(s)",
+			},
+			&cli.StringSliceFlag{
+				Name:        "t",
+				Required:    false,
+				Destination: &flTransformations,
+				Usage:       "The transformation(s) to apply to the GitOps configuration.",
+			},
+			&cli.BoolFlag{
+				Name:  "force",
+				Usage: "Overwrite existing files.",
+			},
+		},
+		Action: func(c *cli.Context) error {
+			var err error
+
+			// verify output directory
+			if outputDirectory == "" {
+				return errors.New("-dir must be specified")
+			}
+			if _, err := os.Stat(outputDirectory); os.IsNotExist(err) {
+				return fmt.Errorf("output directory does not exist: %s", outputDirectory)
+			}
+			if force {
+				err = os.RemoveAll(outputDirectory)
+				if err != nil {
+					return fmt.Errorf("Error removing output directory: %v\n", err)
+				}
+				err = os.MkdirAll(outputDirectory, 0o755)
+				if err != nil {
+					return fmt.Errorf("Error recreating output directory: %v\n", err)
+				}
+			}
+
+			// verify filenames
+			totalFilenames := len(flFilenames.Value())
+			if totalFilenames == 0 {
+				return errors.New("-f must be specified")
+			}
+			for _, flFilename := range flFilenames.Value() {
+				if strings.TrimSpace(flFilename) == "" {
+					return errors.New("file name cannot be empty")
+				}
+				if len(filepath.Base(flFilename)) > filenameMaxLength {
+					return fmt.Errorf("file name must be less than %d characters: %s", filenameMaxLength, filepath.Base(flFilename))
+				}
+			}
+
+			// verify transformations
+			var transformationsToApply []string
+			var unknownTransformations []string
+			if len(flTransformations.Value()) == 0 {
+				fmt.Fprintln(w, "[!] No transformations specified, will attempt to apply all transformations")
+				transformationsToApply = slices.Collect(maps.Keys(transformations))
+			}
+			for _, flTransformation := range flTransformations.Value() {
+				if _, ok := transformations[flTransformation]; !ok {
+					unknownTransformations = append(unknownTransformations, flTransformation)
+				} else {
+					transformationsToApply = append(transformationsToApply, flTransformation)
+				}
+			}
+			if len(unknownTransformations) > 0 {
+				return fmt.Errorf("unknown transformations specified: %s", strings.Join(unknownTransformations, ", "))
+			}
+
+			filesToTransform, err := filesToOutputDirectory(flFilenames.Value(), outputDirectory)
+			if err != nil {
+				return err
+			}
+
+			for _, flTransformation := range transformationsToApply {
+				transformFunc := transformations[flTransformation]
+				fmt.Fprintf(w, "[+] Applying transformation '%s' to GitOps configuration files...\n", flTransformation)
+				err := transformFunc(filesToTransform)
+				if err != nil {
+					return err
+				}
+			}
+
+			return nil
+		},
+	}
+}
+
+func filesToOutputDirectory(files []string, outputDir string) ([]string, error) {
+	var outputFiles []string
+	for _, file := range files {
+		filename := filepath.Base(file)
+		outputFile := filepath.Join(outputDir, filename)
+
+		if err := os.MkdirAll(outputDir, 0o755); err != nil {
+			return nil, fmt.Errorf("failed to create output directory: %w", err)
+		}
+
+		content, err := os.ReadFile(file)
+		if err != nil {
+			return nil, fmt.Errorf("failed to read file %s: %w", file, err)
+		}
+
+		if err := os.WriteFile(outputFile, content, 0o644); err != nil {
+			return nil, fmt.Errorf("failed to write output file: %w", err)
+		}
+		outputFiles = append(outputFiles, outputFile)
+	}
+	return outputFiles, nil
+}
